import React, { useState } from "react";
import { Image, Row, Col, Popover, Button, Typography } from "antd";
import { IncidentsResp } from "../../models/response";
import moment from "moment";
import styled from "styled-components";

import fallBackImage from "../../images/bike_photo_placeholder.svg";

export const Reporter = ({
  media,
  title,
  description,
  occurred_at,
  updated_at,
  type,
  location_description,
  location_type,
  address,
  id,
}: IncidentsResp) => {
  const FALLBACK =
    "data:image/png;base64,";
  const truncate = (str: string) => {
    if (showMore === false) {
      return (
        <div
          onClick={() => {
            setShowMore(true);
          }}
        >
          <span style={{ overflow: "hidden", textOverflow: "ellipsis" }}>
            {str.substring(0, 7)} ...
          </span>
        </div>
      );
    } else {
      return (
        <span
          onClick={() => {
            setShowMore(false);
          }}
        >
          <p>{description}</p>
        </span>
      );
    }
  };
  const [showMore, setShowMore] = useState<boolean>(false);

  const content = (
    <div>
      {description ? (
        <div>
          <strong>Description</strong> <p>{description}</p>
        </div>
      ) : (
        ""
      )}
      {location_description ? (
        <div>
          <strong>location_description</strong> <p>{location_description}</p>
        </div>
      ) : (
        ""
      )}
      {location_type ? (
        <div>
          <strong>location_type</strong> <p>{location_type}</p>
        </div>
      ) : (
        ""
      )}
    </div>
  );

  const { Text, Title } = Typography;
  return (
    <WrapperContainer key={id}>
      <Row>
        <Col>
          <Image width={200} src={media ? media : fallBackImage} />
        </Col>
        <Col>
          <ul>
            <li>{title ? <span>{title}</span> : ""}</li>
            <li>
              {occurred_at ? (
                <span>
                  When:  
                  {moment.unix(occurred_at).format("DD/MM/YYYY")}
                </span>
              ) : (
                ""
              )}
            </li>
            <li>{address ? <p>Address: {address}</p> : ""}</li>
            <li>
              {updated_at ? (
                <span>
                  Last Update: 
                  {moment.unix(occurred_at).startOf("day").fromNow()}
                </span>
              ) : (
                ""
              )}
            </li>
            <li>{type ? <span>Type: {type}</span> : ""}</li>
          </ul>
        </Col>
        <Col>
          <Popover content={content} title="More info" trigger="hover">
            <Button>Hover me</Button>
          </Popover>
        </Col>
      </Row>
    </WrapperContainer>
  );
};

const WrapperContainer = styled.div`
  flex-wrap: wrap;
  li {
    list-style-type: none;
    text-align: left;
  }
`;
